import React from 'react';
import { useLocation } from 'react-router-dom';
import FaqQueAnsList from './FaqQueAnsList';
import HomeFaq from './FAQData';

const location = useLocation();

const sepFaq = () => {
    if (location.pathname === "/") {
        const FAQ = HomeFaq.map(item => {
            return (<>
                <FaqQueAnsList key={item.id} Ans={item.ans} colId={item.id} Que={item.que} />
            </>)
        })
    }
}



const FAQListGroup = () => {
    return (<>
        <ul className="list-group mt-5 pt--5">
            {FAQ}
        </ul>
    </>)
}

export default FAQListGroup;
