import React, { useEffect, useState } from 'react';
import axios from 'axios';
import GoldBars from './Images/gold-bars.svg'

const LiveGoldPrice = () => {
    const [price, setPrice] = useState("")

    async function LiveGoldPrice() {
        const res = await axios.get("https://metals-api.com/api/latest?access_key=uybvgvao7c9p5mlg2vm289k4t18o6l12zz9183cp8egpw542p2i6xyu78yr4&base=INR&symbols=XAU").then(() => setPrice(res.data.rates.XAU / 28.3495)).catch(err => console.log(err))
    }

    useEffect(() => {
        LiveGoldPrice();
    });

    return (<>
        <div>
            <img src={GoldBars} alt="Gold Bars" style={{ height: "2rem", width: "2rem" }} />
            <h2></h2>
        </div>
    </>);
}

export default LiveGoldPrice;
